declare var url: any;
declare var req: any;
declare var debug: any;
declare var debugSensitive: any;
declare var httpntlm: any;
declare var VERSION: any;
/**
 * A class representing the http client
 * @param {Object} [options] Options object. It allows the customization of
 * `request` module
 *
 * @constructor
 */
declare class HttpClient {
    constructor(options: any);
    /**
     * Build the HTTP request (method, uri, headers, ...)
     * @param {String} rurl The resource url
     * @param {Object|String} data The payload
     * @param {Object} exheaders Extra http headers
     * @param {Object} exoptions Extra options
     * @returns {Object} The http request object for the `request` module
     */
    buildRequest(rurl: any, data: any, exheaders: any, exoptions: any): {
        uri: any;
        method: string;
        headers: {
            'User-Agent': string;
            'Accept': string;
            'Accept-Encoding': string;
            'Accept-Charset': string;
            'Connection': string;
            'Host': string;
        };
        followAllRedirects: boolean;
    };
    /**
     * Handle the http response
     * @param {Object} The req object
     * @param {Object} res The res object
     * @param {Object} body The http body
     * @param {Object} The parsed body
     */
    handleResponse(req: any, res: any, body: any): any;
    isNtlmAuthRequired(ntlmSecurity: any, methodName: any): boolean;
    request(rurl: any, data: any, callback: any, exheaders: any, exoptions: any): any;
}
